import React from 'react';
import './About.css';
import ZeroWaveIntro from "../components/AboutVideo"
export default function About(){
  return (
    <section id="about" className="about">
      <div className="about-content container">
        <div className="about-text">
          <h2 style={{color:" var(--primary-cyan)"}}>About <span className="highlight">ZeroWaveLabs</span></h2>
          <p className="lead">At <strong>ZeroWaveLabs</strong>, we believe great ideas should never be held back by limited resources.</p>
          <div className="about-features">
            <div className="feature-item">
              <div className="feature-icon">üéÅ</div>
              <div className="feature-text"><h4>Free Resources</h4><p>Python tools, front-end templates, 3D models - all available to the community</p></div>
            </div>
            <div className="feature-item">
              <div className="feature-icon">üíº</div>
              <div className="feature-text"><h4>Custom Services</h4><p>Tailored solutions for startups and businesses</p></div>
            </div>
            <div className="feature-item">
              <div className="feature-icon">üåç</div>
              <div className="feature-text"><h4>Community-Driven</h4><p>Blending creativity with technology</p></div>
            </div>
            <div className="feature-item">
              <div className="feature-icon">‚úîÔ∏è</div>
              <div className="feature-text"><h4>Multi-Skilled Team</h4><p>From code to design to blockchain to marketing</p></div>
            </div>
          </div>
        </div>
        <div className="about-visual">
          <div className="about-image">
              <div className="relative w-full h-screen overflow-hidden flex flex-col justify-center items-center bg-[#0a0e27]">
      <style>{`
        @keyframes sparkle {
          0%, 100% { opacity: 0; transform: scale(0); }
          50% { opacity: 1; transform: scale(1.2); }
        }
        @keyframes wave-move {
          0% { transform: translateX(-100%); }
          100% { transform: translateX(100%); }
        }
        @keyframes openingReveal {
          0% { opacity: 0; transform: scale(0.5) translateY(20px); letter-spacing: 20px; }
          40% { opacity: 1; transform: scale(1) translateY(0); letter-spacing: 8px; }
          100% { opacity: 0; transform: scale(0.8) translateY(-20px); }
        }
        @keyframes logoAppear {
          to { opacity: 1; transform: scale(1) rotateZ(0deg); }
        }
        @keyframes drawCircle {
          to { stroke-dashoffset: 0; }
        }
        @keyframes waveAppear {
          to { opacity: 1; }
        }
        @keyframes textFadeIn {
          to { opacity: 1; }
        }
        @keyframes slideUpServices {
          to { opacity: 1; transform: translateX(-50%) translateY(-10px); }
        }
        @keyframes iconFlip {
          to { opacity: 1; transform: translateY(0) rotateY(0deg); }
        }
        @keyframes iconShine {
          0% { transform: translateX(-100%) translateY(-100%); }
          100% { transform: translateX(100%) translateY(100%); }
        }
        @keyframes labelsFade {
          to { opacity: 0.7; }
        }
        @keyframes fadeInOut {
          0% { opacity: 0; transform: translateY(-20px) scale(0.9); }
          50% { opacity: 1; transform: translateY(0) scale(1); }
          100% { opacity: 0.5; transform: translateY(0) scale(1); }
        }
        @keyframes gradientShift {
          0%, 100% { filter: hue-rotate(0deg); }
          50% { filter: hue-rotate(10deg); }
        }
        @keyframes statsAppear {
          to { opacity: 1; }
        }
        @keyframes statSlide {
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes rotate {
          100% { transform: translate(-50%, -50%) rotate(360deg); }
        }
        @keyframes fadeIn {
          to { opacity: 0.12; }
        }
        @keyframes finalReveal {
          to { opacity: 1; }
        }
        @keyframes flash {
          0% { opacity: 0; }
          50% { opacity: 1; }
          100% { opacity: 0; }
        }
        @keyframes loadProgress {
          0% { width: 0%; }
          100% { width: 100%; }
        }

        .particle {
          animation: sparkle 3s ease infinite;
        }
        .wave-line {
          animation: wave-move 3s linear infinite;
        }
        .opening-text {
          animation: openingReveal 2.5s ease forwards;
        }
        .logo-wrapper {
          animation: logoAppear 1.5s ease 2s forwards;
        }
        .circle-animation {
          stroke-dasharray: 176;
          stroke-dashoffset: 176;
          animation: drawCircle 1.5s ease 2.2s forwards;
        }
        .wave-path {
          opacity: 0;
          animation: waveAppear 1s ease 2.8s forwards;
        }
        .company-name {
          opacity: 0;
          animation: textFadeIn 0.8s ease 3s forwards;
        }
        .tagline-text {
          opacity: 0;
          animation: textFadeIn 0.8s ease 3.3s forwards;
        }
        .services-showcase {
          animation: slideUpServices 1s ease 4s forwards;
        }
        .service-icon {
          transform: translateY(50px) rotateY(90deg);
          opacity: 0;
          animation: iconFlip 0.6s ease forwards;
        }
        .service-icon:nth-child(1) { animation-delay: 4.2s; }
        .service-icon:nth-child(2) { animation-delay: 4.4s; }
        .service-icon:nth-child(3) { animation-delay: 4.6s; }
        .service-icon:nth-child(4) { animation-delay: 4.8s; }
        .service-icon:nth-child(5) { animation-delay: 5s; }
        .service-icon:nth-child(6) { animation-delay: 5.2s; }
        .service-icon::before {
          content: '';
          position: absolute;
          top: -50%;
          left: -50%;
          width: 200%;
          height: 200%;
          background: linear-gradient(45deg, transparent, rgba(0, 229, 255, 0.3), transparent);
          animation: iconShine 3s ease infinite;
        }
        .service-labels {
          animation: labelsFade 1s ease 5.5s forwards;
        }
        .slogan {
          animation: fadeInOut 2.5s ease 5s forwards;
        }
        .slogan span {
          background: linear-gradient(90deg, #33e8ff, #66f0ff, #33e8ff);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          animation: gradientShift 3s ease infinite;
        }
        .stats-container {
          animation: statsAppear 0.8s ease 6s forwards;
        }
        .stat-item {
          transform: translateY(15px);
          opacity: 0;
          animation: statSlide 0.4s ease forwards;
        }
        .stat-item:nth-child(1) { animation-delay: 6.2s; }
        .stat-item:nth-child(2) { animation-delay: 6.4s; }
        .stat-item:nth-child(3) { animation-delay: 6.6s; }
        .orbit {
          animation: rotate linear infinite;
        }
        .orbit-1 {
          animation-duration: 25s;
        }
        .orbit-2 {
          animation-duration: 35s;
          animation-direction: reverse;
        }
        .orbit-3 {
          animation-duration: 45s;
        }
        .orbital-container {
          opacity: 0;
          animation: fadeIn 1s ease forwards;
        }
        .final-message {
          animation: finalReveal 0.8s ease 7s forwards;
        }
        .flash {
          animation: flash 0.8s ease 7.5s forwards;
        }
        .loading-progress {
          animation: loadProgress 8s ease forwards;
        }
        .skip-btn {
          opacity: 0;
          animation: fadeIn 1s ease 1s forwards;
        }
        .particle-grid {
          opacity: 0;
          animation: fadeIn 0.5s ease forwards;
        }

        @media (max-width: 768px) {
          .opening-text {
            font-size: 14px;
            letter-spacing: 4px;
          }
        }
      `}</style>

      {/* Background gradient */}
      <div className="absolute inset-0 bg-gradient-radial from-[#141829] to-[#0a0e27]" style={{
        background: 'radial-gradient(circle at center, #141829 0%, #0a0e27 100%)'
      }} />

      {/* Orbital Rings */}
      <div className="orbital-container absolute w-[700px] h-[700px]">
        <div className="orbit orbit-1 absolute border border-[#00e5ff] rounded-full w-[350px] h-[350px] top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 shadow-[0_0_20px_rgba(0,229,255,0.2)]" />
        <div className="orbit orbit-2 absolute border border-[#00e5ff] rounded-full w-[500px] h-[500px] top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 shadow-[0_0_20px_rgba(0,229,255,0.2)]" />
        <div className="orbit orbit-3 absolute border border-[#00e5ff] rounded-full w-[650px] h-[650px] top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 shadow-[0_0_20px_rgba(0,229,255,0.2)]" />
      </div>

      {/* Particle Grid */}
      <div className="particle-grid absolute w-full h-full">
        {particles.map(p => (
          <div
            key={p.id}
            className="particle absolute w-[3px] h-[3px] bg-[#33e8ff] rounded-full"
            style={{
              left: `${p.left}%`,
              top: `${p.top}%`,
              animationDelay: `${p.delay}s`,
              animationDuration: `${p.duration}s`
            }}
          />
        ))}
      </div>

      {/* Wave Background */}
      <div className="absolute w-[200%] h-[200%] -top-1/2 -left-1/2 opacity-15">
        {[20, 35, 50, 65, 80].map((top, i) => (
          <div
            key={i}
            className="wave-line absolute w-full h-[2px]"
            style={{
              top: `${top}%`,
              background: 'linear-gradient(90deg, transparent, #33e8ff, transparent)',
              animationDelay: `${i * 0.5}s`
            }}
          />
        ))}
      </div>

      {/* Opening Text */}
      <div className="opening-text absolute top-[25%] text-lg tracking-[8px] uppercase text-[#33e8ff]">
        Welcome to Innovation
      </div>

      {/* Main Logo */}
      <div className="logo-wrapper relative opacity-0 scale-[0.3] rotate-[-10deg]">
        <svg className="w-[300px] h-[75px] md:w-[500px] md:h-[125px]" viewBox="0 0 400 100" xmlns="http://www.w3.org/2000/svg">
          <circle cx="50" cy="50" r="28" fill="none" stroke="#33e8ff" strokeWidth="3" opacity="0.9" className="circle-animation"/>

          <path d="M 30 50 Q 40 35, 50 50 T 70 50" 
                fill="none" 
                stroke="#33e8ff" 
                strokeWidth="2.5"
                strokeLinecap="round"
                className="wave-path">
            <animate attributeName="d" 
                     values="M 30 50 Q 40 35, 50 50 T 70 50;M 30 50 Q 40 65, 50 50 T 70 50;M 30 50 Q 40 35, 50 50 T 70 50"
                     dur="2s" 
                     begin="2.8s"
                     repeatCount="indefinite"/>
          </path>

          <circle cx="35" cy="50" r="2" fill="#33e8ff" className="wave-path">
            <animate attributeName="opacity" values="0.8;0.3;0.8" dur="2s" begin="2.8s" repeatCount="indefinite"/>
          </circle>
          <circle cx="65" cy="50" r="2" fill="#33e8ff" className="wave-path">
            <animate attributeName="opacity" values="0.3;0.8;0.3" dur="2s" begin="2.8s" repeatCount="indefinite"/>
          </circle>

          <text x="95" y="55" fontFamily="'Segoe UI', Arial, sans-serif" fontSize="32" fontWeight="300" fill="#ffffff" className="company-name">
            <tspan fill="#ffffff">Zero</tspan>
            <tspan fill="#33e8ff" fontWeight="500">Wave</tspan>
            <tspan fill="#ffffff">Labs</tspan>
          </text>

          <text x="95" y="75" fontFamily="'Segoe UI', Arial, sans-serif" fontSize="11" fontWeight="400" fill="#33e8ff" letterSpacing="2" opacity="0.9" className="tagline-text">
            INNOVATE THE FUTURE
          </text>

          <line x1="95" y1="82" x2="245" y2="82" stroke="#33e8ff" strokeWidth="0.5" opacity="0.3" className="tagline-text"/>
        </svg>
      </div>

      {/* Slogan */}
      <div className="slogan absolute top-[10%] text-base md:text-[22px] font-light tracking-[4px] opacity-0">
        <span className="font-semibold">EMPOWERING</span> INNOVATION
      </div>

      {/* Service Icons */}
      <div className="services-showcase absolute top-[55%] left-1/2 -translate-x-1/2 flex gap-[15px] md:gap-[30px] opacity-0">
        {['üåê', 'ü§ñ', 'üîó', 'üé®', 'üêç', 'üì±'].map((icon, i) => (
          <div
            key={i}
            className="service-icon relative w-10 h-10 md:w-[60px] md:h-[60px] bg-gradient-to-br from-[rgba(0,229,255,0.1)] to-[rgba(0,229,255,0.05)] border border-[rgba(0,229,255,0.4)] rounded-[15px] flex items-center justify-center text-xl md:text-[28px] overflow-hidden"
          >
            {icon}
          </div>
        ))}
      </div>

      {/* Service Labels */}
      <div className="service-labels absolute left-1/2 -translate-x-1/2 flex gap-[15px] md:gap-[30px] opacity-0" style={{
        top: 'calc(55% + 80px)'
      }}>
        {['Web Dev', 'AI/ML', 'Blockchain', 'Design', 'Python', 'Mobile'].map((label, i) => (
          <div key={i} className="w-10 md:w-[60px] text-[8px] text-center text-[#00e5ff] uppercase tracking-[0.5px] font-medium md:font-normal">
            {label}
          </div>
        ))}
      </div>

      {/* Stats */}
      <div className="stats-container absolute bottom-[15%] left-1/2 -translate-x-1/2 flex gap-[25px] md:gap-10 opacity-0">
        {[
          { number: '500+', label: 'Projects' },
          { number: '98%', label: 'Satisfaction' },
          { number: '24/7', label: 'Support' }
        ].map((stat, i) => (
          <div key={i} className="stat-item text-center">
            <div className="text-xl md:text-2xl font-semibold text-[#33e8ff]" style={{
              textShadow: '0 0 10px rgba(51, 232, 255, 0.4)'
            }}>
              {stat.number}
            </div>
            <div className="text-[7px] md:text-[8px] text-white/70 uppercase tracking-[0.8px] mt-0.5">
              {stat.label}
            </div>
          </div>
        ))}
      </div>

      {/* Final Message */}
      <div className="final-message absolute bottom-[7%] md:bottom-[7%] left-1/2 -translate-x-1/2 text-center opacity-0">
        <h2 className="text-xs md:text-sm font-light mb-0.5 text-white">
          Ready to <span className="text-[#33e8ff] font-medium">Transform</span> Your Ideas?
        </h2>
        <p className="text-white/60 text-[7px] md:text-[8px] tracking-[0.5px]">
          FREE RESOURCES ‚Ä¢ PREMIUM SERVICES ‚Ä¢ FUTURE-READY SOLUTIONS
        </p>
      </div>

      {/* Flash Effect */}
      <div className="flash absolute w-full h-full opacity-0 pointer-events-none" style={{
        background: 'radial-gradient(circle, rgba(0, 229, 255, 0.4) 0%, transparent 70%)'
      }} />

      {/* Loading Bar */}
      <div className="absolute bottom-[3%] md:bottom-[3%] w-[250px] md:w-[400px] h-[2px] bg-[rgba(0,229,255,0.1)] rounded-[2px] overflow-hidden">
        <div className="loading-progress h-full" style={{
          background: 'linear-gradient(90deg, #0099cc, #00e5ff, #00ffff)'
        }} />
      </div>

      {/* Skip Button */}
      <button
        onClick={handleSkip}
        className="skip-btn absolute bottom-5 right-5 px-6 py-2.5 bg-[rgba(0,229,255,0.1)] border border-[rgba(0,229,255,0.4)] text-[#00e5ff] rounded-[25px] cursor-pointer text-xs uppercase tracking-[1px] transition-all duration-300 hover:bg-[rgba(0,229,255,0.2)] hover:-translate-y-1"
        style={{
          boxShadow: 'none'
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.boxShadow = '0 5px 15px rgba(0, 229, 255, 0.3)';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.boxShadow = 'none';
        }}
      >
        Skip ‚Üí
      </button>
    </div>
          </div>
        </div>
      </div>
    </section>
  );
}
