import React, { useEffect, useState } from 'react';
import './Navbar.css';

export default function Navbar() {
  const [scrolled, setScrolled] = useState(false);
  const [open, setOpen] = useState(false);

  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  return (
    <nav className={scrolled ? 'navbar scrolled' : 'navbar'}>
      <div className="nav-container container">
        <a href="#home" className="logo">
          <svg className="logo-svg" viewBox="0 0 400 100" xmlns="http://www.w3.org/2000/svg">
            <g id="wave-symbol">
              <circle cx="50" cy="50" r="28" fill="none" stroke="#00d4ff" strokeWidth="3" opacity="0.9"/>
              <path d="M 30 50 Q 40 35, 50 50 T 70 50" fill="none" stroke="#00d4ff" strokeWidth="2.5" strokeLinecap="round"/>
            </g>
            <text x="95" y="55" fontFamily="'Segoe UI', Arial" fontSize="32" fontWeight="300" fill="#ffffff">
              <tspan fill="#ffffff">Zero</tspan><tspan fill="#00d4ff" fontWeight="500">Wave</tspan><tspan fill="#ffffff">Labs</tspan>
            </text>
          </svg>
        </a>

        <ul className={open ? 'nav-links active' : 'nav-links'}>
          <li><a href="#home" onClick={()=>setOpen(false)}>Home</a></li>
          <li><a href="#services" onClick={()=>setOpen(false)}>Services</a></li>
          <li><a href="#about" onClick={()=>setOpen(false)}>About</a></li>
          <li><a href="#portfolio" onClick={()=>setOpen(false)}>Portfolio</a></li>
          <li><a href="#team" onClick={()=>setOpen(false)}>Team</a></li>
          <li><a href="#contact" onClick={()=>setOpen(false)}>Contact</a></li>
        </ul>

        <button className="cta-button">Get Started</button>

        <div className={open ? 'mobile-menu-toggle active' : 'mobile-menu-toggle'} onClick={() => setOpen(!open)}>
          <span></span><span></span><span></span>
        </div>
      </div>
    </nav>
  );
}
